import React,{Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router , Link,Route} from 'react-router-dom'

class App extends Component{
  constructor(props){
     super(props);
     this.state={
      username:'',
       email:[] // we can write anything 
     }
  }
    update=(index,e)=> {

           const up=this.state.email;
     up[index]=this.state.username;
  this.setState({
       arr:up,
       name:''
  })
    e.preventDefault();
    const user = {
      username: this.state.username
    }
    axios.post('http://localhost:5000/user/update/' +index, user)
      .then(res => console.log(res.data));
  }


     editItem=(index,e)=>{
   axios.get('http://localhost:5000/user/')
      .then(res => console.log(res.data));
    this.setState({
        username:this.state.email[index]
    })
   }
componentDidMount(){
axios
.get(`http://localhost:5000/user/`)  
.then(res=>{

    this.setState({email:res.data});
});
    axios.get('http://localhost:5000/user/')
      .then(response => {
        this.setState({
          username: response.data.username,

        })   
      })
      .catch(function (error) {
        console.log(error);
      })
}

  AddItem=(e)=> {
    const user = {
      username: this.state.username
    }
        axios.post('http://localhost:5000/user/add', user)
      .then(res => console.log(res.data));
    this.setState({
      username: ''
    })
    window.location = '/';
  }
handleChnage=(e)=>{
 this.setState({username:e.target.value})
}
  removeItem=(id)=> {
    axios.delete('http://localhost:5000/user/'+id)
      .then(response => { console.log(response.data)});
    this.setState({
      email: this.state.email.filter(el => el._id !== id)
    })
  }
   render(){
     return (
 <div>

  <div>
  </div>
<input type="text"
name="username"
onChange={this.handleChnage}
placeholder="enter your username"
value={this.state.username}
/>
<button  onClick={this.AddItem}>ADD Item</button>
      <ul>
        {
          this.state.email.map(person=>(
              <li key={person.id}> 
         {person.username} 
               <button onClick={this.removeItem.bind(this,person._id)}>Delete</button>
               <button onClick={this.editItem.bind(this,person._id)}>Update</button>
               <button onClick={this.update.bind(this,person._id)}>Edit</button>

              </li> 
            ))
        }
        </ul>
        </div>
      );
   }
}

export default App;
