 <div>

<input type="text"
name="username"
onChange={this.handleChnage}
value={this.state.username}
/>
<button  onClick={this.state.AddItem}>ADD Item</button>
      <ul>
        {
          this.state.email.map(person=>(
              <li key={person.id}>  {person.username} </li> 

