import React, { Component } from 'react';
import axios from 'axios';
const Exercise = props => (
  <tr>
    <td>{props.exercise.username}</td>
    <td>
      <a href={"/edit/"+props.exercise._id}>edit</a> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
    </td>
  </tr>
)
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      users:[]
    }
  }

  onChangeUsername=(e)=> {
    this.setState({
      username: e.target.value
    })
  }

  onSubmit=(e)=>{
    e.preventDefault();
    const user = {
      username: this.state.username
    }
    console.log(user);
    axios.post('http://localhost:5000/user/add', user)
      .then(res => console.log(res.data));

    this.setState({
      username: ''
    })
  }
  componentDidMount() {
    axios.get('http://localhost:5000/user/')
      .then(response => {
        this.setState({ users: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteExercise=(id)=> {
    axios.delete('http://localhost:5000/user/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      users: this.state.users.filter(el => el._id !== id)
    })
  }

  exerciseList=(e)=> {
    return this.state.users.map(currentexercise => {
      return <Exercise exercise={currentexercise} 
      deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
    })
  }



  render() {
    return (
      <div>
        <h3>Create New User</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group"> 
            <label>Username: </label>
            <input  type="text"
                required
                className="form-control"
                value={this.state.username}
                onChange={this.onChangeUsername}
                />
          </div>
          <div className="form-group">
            <input type="submit" value="Create User" className="btn btn-primary" />
          </div>
        </form>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>

              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.exerciseList() }
          </tbody>
        </table>
      </div>
    )
  }
}
