import React,{Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router , Link} from 'react-router-dom'

const Exercise = props => (
  <tr>
    <td>{props.exercise.username}</td>
    <td>
    <Router>
      <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
      </Router>
    </td>
  </tr>
)
class App extends Component{
  constructor(props){
     super(props);
     this.state={
      username:'',
       email:[] // we can write anything 
     }
  }
componentDidMount(){
axios
.get(`http://localhost:5000/user/`)  
.then(res=>{

    this.setState({email:res.data});
});
}
  AddItem=(e)=> {
    const user = {
      username: this.state.username
    }
        axios.post('http://localhost:5000/user/add', user)
      .then(res => console.log(res.data));
    this.setState({
      username: ''
    })
    window.location = '/';
  }
handleChnage=(e)=>{
 this.setState({username:e.target.value})
}
  removeItem=(id)=>{
    axios.delete('http://localhost:5000/user/'+id)
      .then(response => { console.log(response.data)});
    this.setState({
      email: this.state.email.filter(el => el._id !== id)
    })
  }
    exerciseList() {
    return this.state.email.map(currentexercise => {
      return <Exercise exercise={currentexercise} 
      deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
    })
  }
   render(){
     return (
 <div>

  <div>
  </div>
<input type="text"
name="username"
onChange={this.handleChnage}
placeholder="enter your username"
value={this.state.username}
/>
<button  onClick={this.AddItem}>ADD Item</button>
    <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Description</th>
              <th>Duration</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.exerciseList() }
          </tbody>
        </table>
        </div>
      );
   }
}

export default App;
