import React,{Component} from 'react';
import axios from 'axios';
import { BrowserRouter, Link} from 'react-router-dom'

class App extends Component{
  constructor(props){
     super(props);
     this.state={
      username:'',
       email:[] // we can write anything 
     }
  }
componentDidMount(){
axios
.get(`http://localhost:5000/user/`)  
.then(res=>{

    this.setState({email:res.data});
});
}
  AddItem=(e)=> {
    console.log(e);
    const user = {
      username: this.state.username
    }
    console.log(user);
    axios.post('http://localhost:5000/user/add', user)
      .then(res => console.log(res.data));

    this.setState({
      username: ''
    })
  }
handleChnage=(e)=>{
 this.setState({username:e.target.value})
}
  removeItem=(id)=> {
    axios.delete('http://localhost:5000/user/'+id)
      .then(response => { console.log(response.data)});
    this.setState({
      email: this.state.email.filter(el => el._id !== id)
    })
  }



   render(){
     return (

 <div>
<BrowserRouter>
  <div>
  </div>
</BrowserRouter>
<input type="text"
name="username"
onChange={this.handleChnage}
value={this.state.username}
/>
<button  onClick={this.AddItem}>ADD Item</button>
      <ul>
        {
          this.state.email.map(person=>(
              <li key={person.id}> 
               {person.username} 
               <button onClick={this.removeItem.bind(this,person._id)}>Delete</button>
        <BrowserRouter>
       <Link to="/" className="item">Cats</Link>
</BrowserRouter>
              </li> 

            ))
        }
        </ul>


        </div>
      );
   }
}

export default App;
